import React,{useState} from 'react'
import './Auth.css' 
import icon from '../../assets/icon.png'

const Auth = () => {
  const [isSignup   ,setIsSignup] = useState(false)

  const handleSwitch =() => {
     setIsSignup(!isSignup)
  }
  return (
    <section className='auth-section'>
      <div className='auth-container'>
            {!isSignup && <img src={icon} alt='doubt' className='login-logo'></img>}
            <form >
              {
                isSignup && (
                    <label htmlFor="name">
                      <h4>Display Name</h4>
                       <input type="text" name='name' id='name' />
                    </label>

                )
              }
              <label htmlFor="email">
                <h4>Email</h4>
                <input type="email" name='email' id='email' />
              </label>
              <label htmlFor="password">
                <div>
                <h4>Password</h4>
                { !isSignup && <h4>Forgot Password</h4>}
                </div>
                <input type="password" name='password' id='password' />
                { isSignup && <p>Passwords must contian atleast 8 characters<br/> including 1 letter,1 number and 1 symbol</p>                }
              </label>
              {
                isSignup && (
                  <label htmlFor='check'>
                      <input type="checkbox" id='check'/>
                      <p>Agreeing to all conditions </p>
                  </label>
                )
              }
              <button type='submit' className='auth-btn'>{isSignup ? 'Sign up' : 'Log In'}</button>
               {

               }
            </form>
            <p>
              {isSignup ? 'Already Have an Acoount?' : "Don't Have an Account?"}
              <button type='button' className='handle-switch-btn' onClick={handleSwitch}>{isSignup ? "Log in" : 'Sign up'}</button>
            </p>
      </div>
    </section>
  )
}

export default Auth
